import React from 'react';
import SearchBar from "./searchBar";
import axios from 'axios';


class App extends React.Component{
    state={images:[]};

    async getAction(term){
        const response= await axios.get("https://api.unsplash.com/search/photos",{
            params: {
                query:term
            },
            headers:{
                Authorization: 'Client-ID bmDzue1ffMEQZ4lPNEVS8IGgMAz6UkB7rZzlO_HC-GA'
            }
        });

        this.setState({images:response.data.records})
        console.log(term+" on Action");
    }

    render(){
        return (<div className="ui container" ><SearchBar onAction={(term)=>this.getAction(term)} /><hr />Found {this.state.images.length} images results</div>);
    }
}

export default App;
