import React from 'react';
import SearchBar from "./searchBar";
import axios from 'axios';


class App extends React.Component{
    state={images:[]};

    async getAction(term){
        axios.get("https://api.unsplash.com/search/photos",{
            params: {
                query:term
            },
            headers:{
                Authorization: 'Client-ID bmDzue1ffMEQZ4lPNEVS8IGgMAz6UkB7rZzlO_HC-GA'
            }
        }).then(response =>{
            this.setState({images:response.data.records})
        })


        console.log(term+" on Action");
    }

    render(){
        return (<div className="ui container" ><SearchBar onAction={this.getAction} /><hr />Found {this.state.images.length} images results</div>);
    }
}

export default App;
