import React from "react";


class ImageCard extends React.Component{

    constructor(props){
        super(props);
        this.state={spans:0};
        this.imageRef= React.createRef();
    }
    componentDidMount(){
        this.imageRef.current.addEventListener('load',this.spanImage)
        console.log(this.imageRef.current.clientHeight);
    }
    spanImage =() =>{
        const height = this.imageRef.current.clientHeight;
        const spans = Math.floor((height+50) / 150+1)
        this.setState({spans})
    }
    render(){
        return (
        <div class="ui link cards" style={{gridRowEnd: `span ${this.state.spans}`}}>
          <div class="card">
            <div class="image">
              <img ref={this.imageRef} src={this.props.image.urls.regular}
                          alt={this.props.image.description} />
            </div>
            <div class="content">
              <div class="header">Matt Giampietro</div>
            </div>
          </div>
          </div>
        )
    }
}

export default ImageCard;
