import React from 'react';
import SearchBar from "./searchBar";
import axios from 'axios';


class App extends React.Component{
    state={images:[],total:null};

    getAction= async (term)=>{
        const response= await axios.get("https://api.unsplash.com/search/photos",{
            params: {
                query:term
            },
            headers:{
                Authorization: 'Client-ID bmDzue1ffMEQZ4lPNEVS8IGgMAz6UkB7rZzlO_HC-GA'
            }
        });
        console.log(response)
        this.setState({images:response.data.results,total:response.data.total})
    }

    render(){
        return (<div className="ui container" ><SearchBar onAction={this.getAction} /><hr />Found : {this.state.total} &nbsp;Showing: {this.state.images.length} images results</div>);
    }
}

export default App;
